// src/components/ChakraCarousel.js
import React, { useState, useEffect } from 'react';
import './ChakraCarousel.css';

const chakraData = [
  { id: 1, title: 'Root Chakra', description: 'Foundation and grounding.', image: '/images/root-chakra.jpg' },
  { id: 2, title: 'Sacral Chakra', description: 'Creativity and passion.', image: '/images/Sacral-Chakra.webp' },
  { id: 3, title: 'Solar Plexus Chakra', description: 'Empowerment and confidence.', image: '/images/plexus-solar.jpg' },
  { id: 4, title: 'Heart Chakra', description: 'Love and compassion.', image: '/images/heart.webp' },
  { id: 5, title: 'Throat Chakra', description: 'Authentic expression.', image: '/images/throat.jpg' },
  { id: 6, title: 'Third Eye Chakra', description: 'Intuition and insight.', image: '/images/ajna.jpeg' },
  { id: 7, title: 'Crown Chakra', description: 'Connection to the divine.', image: '/images/crown.jpg' },
];

function ChakraCarousel() {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % chakraData.length);
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="chakra-carousel">
      <div className="chakra-slide">
        <img src={chakraData[currentIndex].image} alt={chakraData[currentIndex].title} className="chakra-image" />
        <h4>{chakraData[currentIndex].title}</h4>
        <p>{chakraData[currentIndex].description}</p>
      </div>
    </div>
  );
}

export default ChakraCarousel;
