import React from "react";
import { useParams } from "react-router-dom";
import Navbar from "./Navbar";
import Footer from "./Footer";

const EventPage = () => {
    const { id } = useParams();

    const offerings = [
        {
            id: 1,
            title: "Meditation Session",
            description: "A guided meditation session for relaxation.",
            price: 50,
            image: "/images/anass.jpg",
        },
        {
            id: 2,
            title: "Reiki Healing",
            description: "A reiki session to balance your energy.",
            price: 70,
            image: "/images/reiki.jpg",
        },
        {
            id: 3,
            title: "Kundalini Activation",
            description: "An intensive Kundalini activation session.",
            price: 100,
            image: "/images/acroyoga.jpg",
        },
    ];

    const event = offerings.find((offering) => offering.id === parseInt(id));

    return (
        <div>
            <Navbar />
            <main style={styles.main}>
                <h1 style={styles.title}>{event.title}</h1>
                <img src={event.image} alt={event.title} style={styles.image} />
                <p style={styles.description}>{event.description}</p>
                <p style={styles.price}>Price: â‚¬{event.price}</p>
                <form style={styles.form}>
                    <h2>Checkout</h2>
                    <label>
                        Name:
                        <input type="text" placeholder="Your Name" required />
                    </label>
                    <label>
                        Bank Details:
                        <input type="text" placeholder="Bank Account Number" required />
                    </label>
                    <button type="submit" style={styles.submitButton}>
                        Confirm Booking
                    </button>
                </form>
            </main>
            <Footer />
        </div>
    );
};

const styles = {
    main: {
        padding: "20px 30px",
    },
    title: {
        textAlign: "center",
        fontSize: "2rem",
        marginBottom: "20px",
    },
    image: {
        display: "block",
        margin: "0 auto",
        maxWidth: "100%",
        borderRadius: "5px",
    },
    description: {
        textAlign: "center",
        fontSize: "1.2rem",
        margin: "20px 0",
    },
    price: {
        textAlign: "center",
        fontSize: "1.5rem",
        fontWeight: "bold",
        marginBottom: "20px",
    },
    form: {
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        gap: "10px",
        maxWidth: "400px",
        margin: "0 auto",
        border: "1px solid #ddd",
        padding: "20px",
        borderRadius: "10px",
    },
    submitButton: {
        background: "linear-gradient(90deg, #0070f3, #1dd1a1)",
        color: "white",
        padding: "10px 20px",
        border: "none",
        borderRadius: "5px",
        cursor: "pointer",
    },
};

export default EventPage;
